import React, { useState } from 'react';
import axios from 'axios';


export const ChatBubble = ({ speaker, content, token }) => {
    const [isCloneContentVisible, setCloneContentVisible] = useState(false);
    const [isNotCloneContentVisible, setNotCloneContentVisible] = useState(false);
    const [confidence, setConfidence] = useState(null);
    const [reason, setReason] = useState("");

    const handleCloneButtonClick = () => {
        setCloneContentVisible(true);
        setNotCloneContentVisible(false);
    };

    const handleNotCloneButtonClick = () => {
        setCloneContentVisible(false);
        setNotCloneContentVisible(true);
    };


    return (
        <>
            {speaker === "digital clone" ?
                <div className='clonechat'>
                    <div className='imgAndName'>
                        <img src='/s512.png' alt='logo' />
                        <h4>Suyoun</h4>
                    </div>
                    <div className='chatbubble'>{content}</div>
                    <div className='clonebuttoncontainer'>
                        <button className='isClone' onClick={handleCloneButtonClick}>내 클론이다</button>
                        <button className='isNotClone' onClick={handleNotCloneButtonClick}>내 클론 아니다</button>
                    </div>
                    {isCloneContentVisible && (
                        <div className='additionalContent'>
                            <div className='additionalText'>1. How closely does your clone resemble your trait?</div>
                            <div className='confidence-scale'>
                                <button onClick={() => setConfidence(20)}>20%</button>
                                <button onClick={() => setConfidence(40)}>40%</button>
                                <button onClick={() => setConfidence(60)}>60%</button>
                                <button onClick={() => setConfidence(80)}>80%</button>
                                <button onClick={() => setConfidence(100)}>100%</button>
                            </div>
                            <div className='additionalText2'>2. Enter the reason</div>
                            <textarea className='cloneReason' placeholder='Type here' value={reason} onChange={(e) => setReason(e.target.value)}></textarea>
                            <div className='content-buttons'>
                                <button onClick={() => setCloneContentVisible(false)}>Cancel</button>
                                <button onClick={handleSave}>Save</button>
                            </div>
                        </div>
                    )}
                    {isNotCloneContentVisible && (
                        <div className='additionalContent'>
                            <div className='additionalText'>Please provide feedback on why it's not your clone</div>
                            <textarea className='cloneReason' placeholder='Type your feedback here' value={reason} onChange={(e) => setReason(e.target.value)}></textarea>
                            <div className='content-buttons'>
                                <button onClick={() => setNotCloneContentVisible(false)}>Cancel</button>
                                <button onClick={handleSave}>Submit</button>
                            </div>
                        </div>
                    )}
                </div>
                :
                <div className='userchat'>
                    <div className='chatbubble'>{content}</div>
                </div>
            }
        </>
    );
};
