import React, { useState } from 'react';
import axios from 'axios';


export const Signin = (props) => {

    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");


    const emailHandler = (e) => {
        e.preventDefault();
        setEmail(e.target.value);
    }

    const passwordHandler = (e) => {
        e.preventDefault();
        setPassword(e.target.value);
    }


    const submitHander = async (e) => {
        e.preventDefault();
        axios({
            method: 'post',
            url: '/token',
            data: {
                email: email,
                password: password
            }
        })
        .then((response) => {
            props.setToken(response.data.access_token) //Ïó¨Í∏∞ ÌÜ†ÌÅ∞ÏÇ¨Ïö©
        }).catch((error) => {
            if (error.response) {
            console.log(error.response)
            console.log(error.response.status)
            console.log(error.response.headers)
            }
        })
        setEmail("");
        setPassword("");
    }




    return(
        <div className='signholder'>
                    <form className='signupbox' onSubmit={submitHander}>
                        <div className='signinTitle'>
                        üë§ Create My Digital Clone üë§
                        </div>
                        <input className='signInput' type="email" value={email} onChange={emailHandler} placeholder={'Username'} ></input>
                        <input className='signInput' type="password" value={password} onChange={passwordHandler} placeholder={'Password'} ></input>
                        <button className='loginBtn' type="submit">Î°úÍ∑∏Ïù∏</button>

                    </form>

                    <div className='buttonContainer'>
                        ÌöåÏõêÍ∞ÄÏûÖ

                            <button className='signBtn' onClick={() => props.setIsSignup()} >Í≥ÑÏ†ï ÎßåÎì§Í∏∞</button>
                        </div>




                </div>


    );


};
