import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Validate } from './validate';

export const Signup = (props) => {

    // values
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");

    const [errors, setErrors] = useState({});
    const [submitting, setSubmitting] = useState(false);

    const emailHandler = (e) => {
        e.preventDefault();
        setEmail(e.target.value);
    }

    const passwordHandler = (e) => {
        e.preventDefault();
        setPassword(e.target.value);
    }

    const submitHander = async (e) => {
        setSubmitting(true);
        e.preventDefault();
        await new Promise((r) => setTimeout(r, 1000));
        setErrors(Validate({email, password}));
    }

    useEffect(() => {
        if(submitting) {
            if(Object.keys(errors).length === 0) {
                axios({
                    method: 'post',
                    url: '/signup',
                    data: {
                        email: email,
                        password: password
                    }
                });
                props.setIsSignin();
            }
            setSubmitting(false);
        }
    }, [errors]);


    return(
        <div className='signholder'>
                    <form className='signupbox' onSubmit={submitHander}>
                        <div className='signinTitle'>
                            Create My Digital Clone
                        </div>
                        <input className='signInput' value={email} onChange={emailHandler} placeholder={'Username'} ></input>
                        <input className='signInput' value={password} onChange={passwordHandler} placeholder={'Password'} ></input>

                        <div className='buttonContainer'>
                            <button className='loginBtn' type="submit">Login</button>
                            <button onClick={()=> props.setIsSignin()} className='signBtn' type="submit">Sign </button>
                        </div>



                    </form>





                </div>

    );
};
