import React, { useState } from 'react';


export const ChatBubble = ({ speaker, content }) => {
    const [isPanelVisible, setPanelVisible] = useState(false);

    const handleCloneButtonClick = () => {
        setPanelVisible(true);
    };

    const handleClosePanel = () => {
        setPanelVisible(false);
    };

    return (
        <>
            {speaker === "digital clone" ? 
                <div className='clonechat'>
                    <div className='cloneProfile'> 
                        <img src='/s512.png' alt='logo' />
                        <h4>Suyoun</h4>
                    </div>
                    <div className='chatbubble'>{content}</div>
                    <div className='clonebuttoncontainer'>
                        <button className='isClone' onClick={handleCloneButtonClick}>내 클론이다</button>
                        <button className='isNotClone'>내 클론 아니다</button>
                    </div>
                    {isPanelVisible && (
                        <div className='panel'>
                            <div className='panel-content'>
                                <h3>How closely does your clone resemble your trait?</h3>
                                <div className='confidence-scale'>
                                    <button>1</button>
                                    <button>2</button>
                                    <button>3</button>
                                    <button>4</button>
                                    <button>5</button>
                                </div>
                                <textarea placeholder='Enter the reason'></textarea>
                                <div className='panel-buttons'>
                                    <button onClick={handleClosePanel}>Cancel</button>
                                    <button>Save</button>
                                </div>
                            </div>
                        </div>
                    )}
                </div>
                :
                <div className='userchat'>
                    <div className='chatbubble'>{content}</div>
                </div>
            }
        </>
    );
};
